insert into halls(id, name, row_count, place_count, description) values(1, 'VIP', 4, 4, 'Cамый камерный и индивидуальный. Здесь всего 16 комфортных кресел-реклайнеров, увеличенное расстояние между креслами и рядами, кнопка вызова официанта, а также профессиональное караоке оборудование — именно поэтому  этот зал чаще остальных востребован для проведения закрытых персональных кинопоказов и проведения частных мероприятий, среди которых дни рождения, помолвки, девичники и др.');
insert into halls(id, name, row_count, place_count, description) values(2, 'ATMOS', 5, 6, 'Название зала соответствует используемой в нём технологии Dolby Atmos — системе цифрового многоканального звука, обеспечивающей максимально реалистичное озвучивание всех происходящих событий фильма и позволяющей  приблизить атмосферу в кинотеатре к звуковой среде фильма. Dolby Atmos – это новейшая разработка, которая «… позволяет зрителям не просто наблюдать за событиями на экране, а ощутить себя вовлечённым в действие — то, о чём так долго мечтали многие режиссёры»');
insert into halls(id, name, row_count, place_count, description) values(3, 'AURO', 5, 4, 'Зал оборудован широкими креслами-реклайнерами, которые были разработаны специально по заказу Cinema GRAND PALACE. Каждое кресло снабжено индивидуальными столиками и удобными подушками.  В зале установлена 11.1-канальная технология воспроизведения звука Auro 3D by Barco, которая превращает звуковую дорожку фильма в самое реалистичное и естественное звучание с полным эффектом погружения.');